    def count_targets(self):
        ff_hv, kill_hv = count_hv_targets(self.board, self)
        ff_diag, kill_diag = count_diag_targets(self.board, self)
        return ff_hv + ff_diag, kill_hv + kill_diag

class Rook(Piece):
