import random
from functools import lru_cache

# all class and function definitions in the code file, if any
possible_moves = ['roll', 'hold']
other = {1: 0, 0: 1}
goal = 50

def hold(state):
    """Apply the hold action to a state to yield a new state:
    Reap the 'pending' points and it becomes the other player's turn."""
    (p, me, you, pending) = state
    return (other[p], you, me + pending, 0)

def roll(state, d):
    """Apply the roll action to a state (and a die roll d) to yield a new state:
    If d is 1, get 1 point (losing any accumulated 'pending' points),
    and it is the other player's turn. If d > 1, add d to 'pending' points."""
    (p, me, you, pending) = state
    if d == 1:
        return (other[p], you, me + 1, 0)  # pig out; other player's turn
    else:
        return (p, me, you, pending + d)  # accumulate die roll in pending

def hold_at(x):
    """Return a strategy that holds if and only if 
    pending >= x or player reaches goal."""
    def strategy(state):
        (p, me, you, pending) = state
        return 'hold' if (pending >= x or me + pending >= goal) else 'roll'
    strategy.__name__ = 'hold_at(%d)' % x
    return strategy

def dierolls():
    "Generate die rolls."
    while True:
        yield random.randint(1, 6)

@lru_cache(maxsize=None)
def Pwin3(me, you, pending):
    "The probability of winning for player with score me to you, and pending."
    if me + pending >= goal:
        return 1
    elif you >= goal:
        return 0
    else:
        Proll = (1 - Pwin3(you, me + 1, 0) +
                 sum(Pwin3(me, you, pending + d) for d in (2, 3, 4, 5, 6))) / 6.
        return (Proll if not pending else
                max(Proll, 1 - Pwin3(you, me + pending, 0)))

def max_wins2(state):
    "The optimal pig strategy chooses an action with the highest win probability."
    def Q_pig(state, action, Pwin):
        if action == 'hold':
            return 1 - Pwin(hold(state))
        if action == 'roll':
            return (1 - Pwin(roll(state, 1))
                    + sum(Pwin(roll(state, d)) for d in (2, 3, 4, 5, 6))) / 6.
        raise ValueError

    def best_action(state, actions, Q, U):
        def EU(action): return Q(state, action, U)
        return max(actions(state), key=EU)

    def pig_actions(state):
        _, _, _, pending = state
        return ['roll', 'hold'] if pending else ['roll']

    return best_action(state, pig_actions, Q_pig, Pwin3)

# main function
def main_solution(strategy1_hold_threshold, strategy2_hold_threshold):
    """Simulate a game of Pig between two strategies and return the winning strategy's name."""
    strategy1 = hold_at(strategy1_hold_threshold)
    strategy2 = hold_at(strategy2_hold_threshold)

    def play_pig(A, B, rolls=dierolls()):
        strategies = [A, B]
        state = (0, 0, 0, 0)
        while True:
            (p, me, you, pending) = state
            if me >= goal:
                return strategies[p].__name__
            elif you >= goal:
                return strategies[other[p]].__name__
            else:
                action = strategies[p](state)
                if action == 'hold':
                    state = hold(state)
                elif action == 'roll':
                    state = roll(state, next(rolls))
                else:
                    return strategies[other[p]].__name__

    winner = play_pig(strategy1, strategy2)
    return winner

