        return product
    return fact_iter(num - 1, num * product)

def hanoi_steps(n, a, b, c):
    steps = []
    def hanoi(n, a, b, c):
        if n == 1:
            steps.append(f"{a} --> {c}")
        else:
            hanoi(n - 1, a, c, b)
            steps.append(f"{a} --> {c}")
            hanoi(n - 1, b, a, c)
    hanoi(n, a, b, c)
    return steps

