class Queue:
    def __init__(self):
        self.size = 0
        self.storage = []

    def __len__(self):
        return len(self.storage)

    def enqueue(self, value):
        self.storage.append(value)
        self.size += 1

    def dequeue(self, index=0):
        if len(self.storage) == 0:
            return None
        self.size -= 1
        return self.storage.pop(index)

class Stack:
    def __init__(self):
        self.size = 0
        self.storage = []

    def __len__(self):
        return len(self.storage)

    def push(self, value):
        self.storage.append(value)
        self.size += 1

    def pop(self):
        if len(self.storage) == 0:
            return None
        self.size -= 1
        return self.storage.pop()

class BSTNode:
    def __init__(self, value):
        self.value = value
        self.left = None
        self.right = None

    def insert(self, value):
        cur_node = self
        new_node = BSTNode(value)
        while True:
            if new_node.value >= cur_node.value:
                if cur_node.right is None:
                    cur_node.right = new_node
                    break
                cur_node = cur_node.right
            elif new_node.value < cur_node.value:
                if cur_node.left is None:
                    cur_node.left = new_node
                    break
                cur_node = cur_node.left

    def contains(self, target):
        cur_node = self
        while True:
            if target == cur_node.value:
                return True
            elif target > cur_node.value:
                if cur_node.right is None:
                    return False
                cur_node = cur_node.right
            elif target < cur_node.value:
                if cur_node.left is None:
                    return False
                cur_node = cur_node.left
            else:
                return False

    def get_max(self):
        cur_node = self
        while cur_node.right is not None:
            cur_node = cur_node.right
        return cur_node.value

    def for_each(self, fn):
        fn(self.value)
        if self.left:
            self.left.for_each(fn)
        if self.right:
            self.right.for_each(fn)

    def in_order_print(self):
        if self.left:
            self.left.in_order_print()
        print(self.value)
        if self.right:
            self.right.in_order_print()

    def bft_print(self):
        q = Queue()
        q.enqueue(self)
        while q.size != 0:
            for i, node in enumerate(q.storage):
                print(node.value)
                if node.left is not None:
                    q.enqueue(node.left)
                if node.right is not None:
                    q.enqueue(node.right)
                q.dequeue(i)

    def dft_print(self):
        s = Stack()
        s.push(self)
        while s.__len__() != 0:
            for node in s.storage:
                if node.left is None:
                    if node.right is not None:
                        s.push(node.right)
                if node.left is not None:
                    s.push(node.left)
                    if node.right is not None:
                        s.push(node.right)
                print(node.value)
            s.pop()
            return

# main function
def main_solution(values, target):
    # values: list of integers to be inserted into the BST
    # target: integer to be searched in the BST

    # Create the BST and insert values
    root = BSTNode(values[0])
    for value in values[1:]:
        root.insert(value)

    # Check if the target is in the BST
    contains_target = root.contains(target)

    # Get the maximum value in the BST
    max_value = root.get_max()

    # Return the results
    return {
        "contains_target": contains_target,
        "max_value": max_value
    }

