# import necessary packages
import copy

# Constants
X = "X"
O = "O"
EMPTY = None

# Function to initialize the board
def initial_state():
    return [[EMPTY, EMPTY, EMPTY],
            [EMPTY, EMPTY, EMPTY],
            [EMPTY, EMPTY, EMPTY]]

# Function to determine the next player
def player(board):
    CntX = sum(row.count(X) for row in board)
    CntO = sum(row.count(O) for row in board)
    return X if CntX == CntO else O

# Function to get all possible actions
def actions(board):
    return {(i, j) for i in range(3) for j in range(3) if board[i][j] == EMPTY}

# Function to get the result of an action
def result(board, action):
    new_board = copy.deepcopy(board)
    new_board[action[0]][action[1]] = player(board)
    return new_board

# Function to determine the winner
def winner(board):
    lines = [
        [(0, 0), (0, 1), (0, 2)],
        [(1, 0), (1, 1), (1, 2)],
        [(2, 0), (2, 1), (2, 2)],
        [(0, 0), (1, 0), (2, 0)],
        [(0, 1), (1, 1), (2, 1)],
        [(0, 2), (1, 2), (2, 2)],
        [(0, 0), (1, 1), (2, 2)],
        [(0, 2), (1, 1), (2, 0)],
    ]
    for line in lines:
        if board[line[0][0]][line[0][1]] == board[line[1][0]][line[1][1]] == board[line[2][0]][line[2][1]] != EMPTY:
            return board[line[0][0]][line[0][1]]
    return None

# Function to check if the game is over
def terminal(board):
    return winner(board) is not None or all(all(cell is not EMPTY for cell in row) for row in board)

# Function to get the utility of the board
def utility(board):
    win = winner(board)
    if win == X:
        return 1
    elif win == O:
        return -1
    else:
        return 0

# Main function to solve the problem
def main_solution(board):
    if terminal(board):
        return utility(board)
    else:
        return minimax(board)

# Function to determine the optimal action using minimax algorithm
def minimax(board):
    if terminal(board):
        return None
    if player(board) == X:
        return max_value(board, float("-inf"), float("inf"))[1]
    else:
        return min_value(board, float("-inf"), float("inf"))[1]

# Helper function for maximizing player
def max_value(board, alpha, beta):
    if terminal(board):
        return [utility(board), None]
    v = float("-inf")
    move = None
    for action in actions(board):
        test = min_value(result(board, action), alpha, beta)[0]
        if test > v:
            v = test
            move = action
        alpha = max(alpha, v)
        if alpha >= beta:
            break
    return [v, move]

# Helper function for minimizing player
def min_value(board, alpha, beta):
    if terminal(board):
        return [utility(board), None]
    v = float("inf")
    move = None
    for action in actions(board):
        test = max_value(result(board, action), alpha, beta)[0]
        if test < v:
            v = test
            move = action
        beta = min(beta, v)
        if alpha >= beta:
            break
    return [v, move]

