import random
from collections import defaultdict

# all class and function definitions in the code file, if any
class Graph:
    def __init__(self, vertices):
        self.V = vertices  # No. of vertices
        self.graph = defaultdict(list)  # default dictionary to store graph

    def addEdge(self, u, v):
        self.graph[u].append(v)

    def DFSUtil(self, v, visited):
        visited[v] = True
        for i in self.graph[v]:
            if visited[i] == False:
                self.DFSUtil(i, visited)

    def getTranspose(self):
        g = Graph(self.V)
        for i in self.graph:
            for j in self.graph[i]:
                g.addEdge(j, i)
        return g

    def isSC(self):
        visited = [False] * (self.V)
        self.DFSUtil(0, visited)
        if any(i == False for i in visited):
            return False
        gr = self.getTranspose()
        visited = [False] * (self.V)
        gr.DFSUtil(0, visited)
        if any(i == False for i in visited):
            return False
        return True

# main function
def main_solution(N, K, edges):
    # N: number of circles (vertices)
    # K: number of arrows (edges)
    # edges: list of tuples representing the edges (u, v) where u -> v

    # Create the graph
    g1 = Graph(N)
    matrix = [[0 for _ in range(N)] for _ in range(N)]

    for u, v in edges:
        g1.addEdge(u, v)
        matrix[u][v] = 1

    # Check for strong connection
    if not g1.isSC():
        return {"error": "Digraph is not strongly connected"}

    # Check for in and out arrows
    strongList = [[0 for _ in range(2)] for _ in range(N)]
    for a in range(N):
        for x in range(N):
            if matrix[a][x] > 0:
                strongList[a][1] = 1
            if matrix[x][a] > 0:
                strongList[a][0] = 1

    count = sum(sum(row) for row in strongList)
    if count != N * 2:
        return {"error": "All circles do not have both an in and an out arrow"}

    # Simulate the game
    checksTotal = [0] * 10
    for mate in range(1, 11):
        currentCircle = 0
        checkList = [0] * N
        checkList[0] = 1
        checkMarkTotal = 0
        checkMarkTally = [0] * N
        checkMarkTally[0] = 1
        total = 0
        iterations = 0
        while True:
            roll = random.randint(0, N - 1)
            if matrix[currentCircle][roll]:
                currentCircle = roll
                checkMarkTotal += 1
                checkMarkTally[currentCircle] += 1
                checkList[currentCircle] = 1
            iterations += 1
            if sum(checkList) == N:
                break
            if iterations > 1000000:
                return {"error": f"Number of checks for game {mate} exceeded 1,000,000"}
        checksTotal[mate - 1] = checkMarkTotal

    # Calculate results
    sum_checks = sum(checksTotal)
    max_checks = max(checksTotal)
    min_checks = min(checksTotal)
    avg_checks = sum_checks / 10

    return {
        "average_checks_per_game": avg_checks,
        "maximum_checks_in_a_single_game": max_checks,
        "minimum_checks_in_a_single_game": min_checks
    }

