import re
import string
from collections import deque

# all class and function definitions in the code file, if any
def parse_data(data):
    steps = {}
    for line in data:
        values = re.match("Step ([A-Z]) must be finished before step ([A-Z]) can begin.", line)
        if values.group(1) not in steps:
            steps[values.group(1)] = []
        steps[values.group(1)].append(values.group(2))
        steps[values.group(1)].sort()
    return steps

def find_starting_step(steps):
    second_steps = [item for i in steps for item in steps[i]]
    queue = deque()
    for c in string.ascii_uppercase:
        if c not in second_steps:
            queue.append(c)
    return queue

# main function
def main_solution(data):
    # Convert the input data to the required format
    steps = parse_data(data)
    queue = find_starting_step(steps)
    result = ""

    while len(queue) > 0:
        curr = queue.popleft()
        while curr in [item for i in steps for item in steps[i]]:
            queue.append(curr)
            curr = queue.popleft()

        if curr in steps:
            for x in steps[curr]:
                if x not in queue:
                    queue.append(x)
            del steps[curr]
        queue = deque(sorted(queue))
        result += curr

    # Return the result as a string
    return result

