def conflict(state, nextX):
    nextY = len(state)
    for i in range(nextY):
        if abs(state[i] - nextX) in (0, nextY - i):
            return True
    return False

def queen(num, state=()):
    for i in range(num):
        if not conflict(state, i):
            if len(state) == num - 1:
                yield (i,)
            else:
                for result in queen(num, state + (i,)):
                    yield (i,) + result

# main function
def main_solution(num_queens):
    # Convert the input to the required format
    num_queens = int(num_queens)

    # Generate the first solution for the N-Queens problem
    solution_generator = queen(num_queens)
    solution = next(solution_generator)

    # Convert the solution to a JSON serializable format
    solution_list = list(solution)

    return solution_list

