def tower(base, h, m):
    """Return base ** base ** ... ** base, where the height is h, modulo m. """
    if m == 1:
        return 0
    if base == 1:
        return 1
    if h == 0:
        return 1
    if h == 1:
        return base % m

    G, t = totient(m)

    if base in G:
        f = base
        for j in range(h-2):
            f = (base ** f) % t
        return (base ** f) % m

    return 100

def totient(m):
    G = set()
    G.add(1)
    d = set()
    for j in range(2, m):
        if j in d:
            continue
        if m % j == 0:
            d.add(j)
            jj = j
            while jj < m:
                jj += j
                d.add(jj)
            continue
        G.add(j)
    return G, len(G)

