        solution[x][y] = 1
        return True

    if is_safe(maze, x, y, n):
        solution[x][y] = 1

        if solve_maze(maze, x+1, y, solution, n):
            return True
        if solve_maze(maze, x, y+1, solution, n):
            return True

        solution[x][y] = 0
        return False

