def l2_norm(f, lim):
    return sqrt(integrate(Abs(f)**2, lim))

def l2_inner_product(a, b, lim):
    return integrate(conjugate(a)*b, lim)

def l2_projection(f, basis, lim):
    r = 0
    for b in basis:
        r += l2_inner_product(f, b, lim) * b
    return r

def l2_gram_schmidt(list, lim):
    r = []
    for a in list:
        if r == []:
            v = a
        else:
            v = a - l2_projection(a, r, lim)
        v_norm = l2_norm(v, lim)
        if v_norm == 0:
            raise ValueError("The sequence is not linearly independent.")
        r.append(v/v_norm)
    return r

def integ(f):
    return integrate(f, (x, -pi, 0)) + integrate(-f, (x, 0, pi))

def series(L):
    r = 0
    for b in L:
        r += integ(b)*b
    return r

def msolve(f, x):
    f = lambdify(x, f)
    x0 = -0.001
    dx = 0.001
    while f(x0 - dx) * f(x0) > 0:
        x0 = x0 - dx
    x_max = x0 - dx
    x_min = x0
    assert f(x_max) > 0
    assert f(x_min) < 0
    for n in range(100):
        x0 = (x_max + x_min)/2
        if f(x0) > 0:
            x_max = x0
        else:
            x_min = x0
    return x0

