    for i in range(1, num_terms):
        L.append(cos(i*x))
        L.append(sin(i*x))
    L[0] /= sqrt(2)
    L = [f/sqrt(pi) for f in L]

    f = series(L)
    x0 = msolve(f.diff(x), x)
    max_value = f.subs(x, x0).evalf()
    wilbraham_gibbs_constant = max_value * pi / 2
    return {"wilbraham_gibbs_constant": float(wilbraham_gibbs_constant)}

