def check(board):
    for x in range(3):
        sumrow = board[x][0] + board[x][1] + board[x][2] 
        if sumrow == 3:
            return -100
        elif sumrow == 0:
            return 100
    for x in range(3):
        sumcol = board[0][x] + board[1][x] + board[2][x] 
        if sumcol == 3:
            return -100
        elif sumcol == 0:
            return 100
    sumdiag1 = board[0][0] + board[1][1] + board[2][2]
    if sumdiag1 == 3:
        return -100
    elif sumdiag1 == 0:
        return 100
    sumdiag2 = board[0][2] + board[1][1] + board[2][0]
    if sumdiag2 == 3:
        return -100
    elif sumdiag2 == 0:
        return 100
    flag = 0  #flag is for checking if any move is possible
    for x in range(3):
        for y in range(3):
            if board[x][y] == -100:
                flag = 1
                return
    if flag == 0:
        return 0

def minmax(game, depth, move_whose):
    if check(game) == 100:
        return 100 - depth, 0
    if check(game) == -100:
        return depth - 100, 0
    if check(game) == 0:
        return 0, 0
    maximum = -10000
    minimum = 10000
    trick = 0
    trickmaxmove = 0
    tricksumminmove = 0
    trickmat = [[-10000 for x in range(3)] for x in range(3)]

    for x in range(3):
        for y in range(3):
            if game[x][y] == -100:
                if move_whose:
                    game[x][y] = 1
                else:
                    game[x][y] = 0
                temp, trick = minmax(game, depth + 1, not move_whose)
                trickmat[x][y] = trick
                if (temp == 100 - depth - 1) and not move_whose:
                    game[x][y] = -100
                    return temp, trick
                if move_whose:
                    tricksumminmove += trick
                    if minimum > temp:
                        minimum = temp
                else:
                    if maximum < temp:
                        maximum = temp
                        trickmaxmove = trick
                game[x][y] = -100
    if move_whose:
        return minimum, tricksumminmove
    else:
        return maximum, trickmaxmove

def ttt_move(board):
    score = [[-10000 for x in range(3)] for x in range(3)]
    trick = [[-10000 for x in range(3)] for x in range(3)]
    for x in range(3):
        for y in range(3):
            if board[x][y] == -100:
                board[x][y] = 0
                score[x][y], trick[x][y] = minmax(board, 0, True)
                score[x][y] = score[x][y] + trick[x][y]
                board[x][y] = -100
    maximum = -10000
    bestx = 1
    besty = 1
    for x in range(3):
        for y in range(3):
            if score[x][y] > maximum:
                maximum = score[x][y]
                bestx = x
                besty = y 
    board[bestx][besty] = 0
    return board

