def main_solution(board):
    # Convert the board to a list of lists if it's not already
    if isinstance(board, str):
        board = [[int(cell) for cell in row.split(',')] for row in board.split(';')]

    # Perform the Tic Tac Toe move
    updated_board = ttt_move(board)

    # Convert the updated board back to a string for JSON serialization
    updated_board_str = ';'.join([','.join([str(cell) for cell in row]) for row in updated_board])

    return updated_board_str

