def main_solution(plate_1, plate_2):
    if not plate_validity(plate_1) or not plate_validity(plate_2):
        return {"error": "Invalid number plate format"}

    plate1_value = plate_value(plate_1)
    plate2_value = plate_value(plate_2)

    if plate1_value == plate2_value:
