def on_map(map, r, c):
    if (r > len(map)-1) or (c > len(map[0])-1) or (r < 0) or (c < 0):
        return False
    else:
        return map[r][c] >= 0

def neighbors(map, r, c):
    neighbors_list = []
    for i in range((r-1),(r+2)):
        for j in range((c-1),(c+2)):
            if on_map(map, i, j) and [i, j] != [r, c]:
                tuple_point = tuple([i, j])
                neighbors_list.append(tuple_point)
    return neighbors_list

def get_island_spots(map, r, c):
    if (on_map(map, r, c) == False) or map[r][c] == 0:
        return []
    to_do = []
    answer = []
    to_do.append((r,c))

    while len(to_do) != 0:
        x = to_do.pop()
        if (map[x[0]][x[1]] != 0) and (not(x in answer)):
            answer.append(x)
            xs = neighbors(map, x[0], x[1])
            for i in xs:
                if (not(i in answer)) and (map[i[0]][i[1]] != 0):
                    to_do.append(i)
    answer.sort()
    return answer

# main function
def main_solution(map, r, c):
    # Convert input to the required format
    map = [[int(x) for x in row] for row in map]

    # Find the island spots
    island_spots = get_island_spots(map, r, c)

    # Convert the result to a JSON serializable format
    return [list(spot) for spot in island_spots]

