# import necessary packages
import math
import copy

# Constants
X = "X"
O = "O"
EMPTY = None
rows = 3
cols = 3

# Function to initialize the board
def initial_state():
    return [[EMPTY, EMPTY, EMPTY],
            [EMPTY, EMPTY, EMPTY],
            [EMPTY, EMPTY, EMPTY]]

# Function to determine the next player
def player(board):
    xcount = sum(row.count(X) for row in board)
    ocount = sum(row.count(O) for row in board)
    return O if xcount > ocount else X

# Function to get all possible actions
def actions(board):
    return [(i, j) for i in range(rows) for j in range(cols) if board[i][j] == EMPTY]

# Function to get the result of an action
def result(board, action):
    if action not in actions(board):
        raise ValueError('Invalid Action')
    board_copy = copy.deepcopy(board)
    board_copy[action[0]][action[1]] = player(board)
    return board_copy

# Function to determine the winner
def winner(board):
    for i in range(rows):
        if all(board[i][j] == X for j in range(cols)):
            return X
        if all(board[i][j] == O for j in range(cols)):
            return O
    for j in range(cols):
        if all(board[i][j] == X for i in range(rows)):
            return X
        if all(board[i][j] == O for i in range(rows)):
            return O
    if all(board[i][i] == X for i in range(rows)) or all(board[i][rows - 1 - i] == X for i in range(rows)):
        return X
    if all(board[i][i] == O for i in range(rows)) or all(board[i][rows - 1 - i] == O for i in range(rows)):
        return O
    return None

# Function to check if the game is over
def terminal(board):
    return winner(board) is not None or not any(EMPTY in row for row in board)

# Function to get the utility of the board
def utility(board):
    return 1 if winner(board) == X else -1 if winner(board) == O else 0

# Function to get the optimal action using minimax
def minimax(board):
    if terminal(board):
        return None
    if player(board) == X:
        return max_value(board)[1]
    else:
        return min_value(board)[1]

def max_value(board):
    if terminal(board):
        return utility(board), None
    v = -math.inf
    best_action = None
    for action in actions(board):
        min_val = min_value(result(board, action))[0]
        if min_val > v:
            v = min_val
            best_action = action
    return v, best_action

def min_value(board):
    if terminal(board):
        return utility(board), None
    v = math.inf
    best_action = None
    for action in actions(board):
        max_val = max_value(result(board, action))[0]
        if max_val < v:
            v = max_val
            best_action = action
    return v, best_action

# main function
def main_solution(board_state):
    """
    Determines the optimal next move for the current player on the given board state.

    Args:
    board_state (list of lists): A 3x3 matrix representing the Tic Tac Toe board. Each element can be 'X', 'O', or None.

    Returns:
    tuple: A tuple representing the optimal move (i, j) where i is the row index and j is the column index.
    """
    return minimax(board_state)

