# all class and function definitions in the code file, if any
class Node(object):
    def __init__(self, state, parent=None, action=None, location=None):
        self.state = state
        self.parent = parent
        self.dimension = len(state)
        self.action = action
        self.location = location
        self.hash = self.gethash()

    def findBlank(self):
        for i in range(self.dimension):
            for j in range(self.dimension):
                if self.state[i][j] == 0:
                    return (i, j)
        raise ValueError("There's no zero in the puzzle error")

    def move(self, xsrc, ysrc, xdest, ydest):
        output = [row[:] for row in self.state]
        output[xsrc][ysrc], output[xdest][ydest] = output[xdest][ydest], output[xsrc][ysrc]
        return output

    def gethash(self):
        temp = []
        total = 0
        for list in self.state:
            for i in list:
                temp.append(i)
        x = len(temp)
        for i in range(x-1):
            greater = 0
            y = x-i-1
            factorial = 1
            if (temp[i]==0):
                continue
            for j in range (i+1,x):
                factorial *= y
                y-=1
                if (temp[j]<temp[i]):
                    greater+=1
            total += greater * factorial
        return total 

    def get_neighbours(self):
        new_states = []
        if (self.location == None):
            (x, y) = self.findBlank()
        else:
            (x, y) = self.location

        if(y-1 >= 0):
            new_states.append(
                Node(self.move(x, y-1, x, y), self, "RIGHT", (x, y-1)))
        if(y+1 < self.dimension):
            new_states.append(
                Node(self.move(x, y+1, x, y), self, "LEFT", (x, y+1)))
        if(x+1 < self.dimension):
            new_states.append(
                Node(self.move(x+1, y, x, y), self, "UP", (x+1, y)))
        if(x-1 >= 0):
            new_states.append(
                Node(self.move(x-1, y, x, y), self, "DOWN", (x-1, y)))

        return new_states

class Puzzle(object):
    def __init__(self, init_state, goal_state):
        self.init_state = init_state
        self.goal_state = goal_state
        self.visited_states = [init_state]
        self.goalhash = Node(goal_state).hash
        self.hashTable = self.generateHashTable()

    def isSolvable(self, state):
        inversions = 0
        singleDim = []
        (y, x) = (0, 0)

        for i in range(0, len(state)):
            for j in range(0, len(state)):
                singleDim.append(state[i][j])
                if state[i][j] == 0:
                    (y, x) = (i, j)
        for i in range(0, len(singleDim)-1):
            for j in range(i+1, len(singleDim)):
                if singleDim[j] and singleDim[i] and singleDim[i] > singleDim[j]:
                    inversions += 1

        if len(state) % 2 == 1:
            if (inversions % 2) == 0:
                return True
            else:
                return False
        else:
            if (y % 2 == 0 and inversions % 2 == 1) or \
               (y % 2 == 1 and inversions % 2 == 0):
                return True
            else:
                return False

    def terminate(self, node):
        output = []
        while(node.parent != None):
            output.insert(0, node.action)
            node = node.parent
        return output

    def generateHashTable(self):
        n = len(self.init_state)
        return [0]*math.factorial(n*n)

    def solve(self):
        if self.isSolvable(Node(self.init_state).state) == False:
            return ["UNSOLVABLE"]

        source = Node(self.init_state)
        self.hashTable[source.hash] = 1
        if (source.hash==self.goalhash):
            return None

        frontier = [source]
        while (len(frontier)!=0):
            node = frontier.pop(0)

            for neighbour in node.get_neighbours():
                if (self.hashTable[neighbour.hash]==0):
                    self.hashTable[neighbour.hash]=1
                    if (neighbour.hash==self.goalhash):
                        return self.terminate(neighbour)
                    frontier.append(neighbour)

# main function
def main_solution(init_state, goal_state):
    puzzle = Puzzle(init_state, goal_state)
    result = puzzle.solve()
    if result is None:
        return "ALREADY_SOLVED"
    elif result == ["UNSOLVABLE"]:
        return "UNSOLVABLE"
    else:
        return result

