def prime_factors(n):
    d = {}
    while not n % 2:
        d[2] = d.get(2, 0) + 1
        n /= 2
    for i in range(3, int(sqrt(n)) + 1, 2):
        while not n % i:
            d[i] = d.get(i, 0) + 1
            n /= i
    if n > 2:
        d[n] = d.get(n, 0) + 1
    return d

