def htmlify(text, fmt):
    n = len(text)
    store = [[] for _ in range(n)]
    for i in range(n):
        for j in fmt:
            if j[1] <= i < j[2]:
                store[i].append(j[0])
    out = ""
    in_progress = []
    end_everything = False
    for i in range(n):
        fmt_arr = store[i]
        for ip in in_progress:
            if ip in fmt_arr:
                continue
            else:
                end_everything = True

        if end_everything:
            while in_progress != []:
                out += end(in_progress.pop())
            end_everything = False

        for f in fmt_arr:
            if f in in_progress:
                continue
            else:
                out += start(f)
                in_progress.append(f)
