# import necessary packages
import math
import random

# Global Variables
human = 'X'
ai = 'O'

# Function to check if three elements are equal and not None
def equals3(a, b, c):
    return a == b and b == c and a != None

# Function to check for a winner or tie
def check_winner(board):
    winner = None
    # Check horizontal
    for i in range(3):
        if equals3(board[i][0], board[i][1], board[i][2]):
            winner = board[i][0]
    # Check vertical
    for i in range(3):
        if equals3(board[0][i], board[1][i], board[2][i]):
            winner = board[0][i]
    # Check diagonal
    if equals3(board[0][0], board[1][1], board[2][2]):
        winner = board[0][0]
    if equals3(board[2][0], board[1][1], board[0][2]):
        winner = board[2][0]
    # Check for a tie
    open_spots = 0
    for row in board:
        for item in row:
            if item == None:
                open_spots += 1

    if winner is None and open_spots == 0:
        return 'tie'
    else:
        return winner

# Minimax algorithm with alpha-beta pruning
def minimax(board, depth, alpha, beta, max_player):
    result = check_winner(board)
    if result == ai:
        return 10
    if result == human:
        return -10
    if result == 'tie':
        return 0

    if max_player:
        max_val = -math.inf
        for i in range(3):
            for j in range(3):
                if board[i][j] == None:
                    board[i][j] = ai
                    cur_eval = minimax(board, depth+1, alpha, beta, False)
                    max_val = max(max_val, cur_eval)
                    board[i][j] = None
                    alpha = max(alpha, cur_eval)
                    if beta <= alpha:
                        break
        return max_val
    else:
        min_val = math.inf
        for i in range(3):
            for j in range(3):
                if board[i][j] == None:
                    board[i][j] = human
                    cur_eval = minimax(board, depth+1, alpha, beta, True)
                    min_val = min(min_val, cur_eval)
                    board[i][j] = None
                    beta = min(beta, cur_eval)
                    if beta <= alpha:
                        break
        return min_val

# Function to find the best move for the AI
def find_best_move(board):
    best_val = None
    best_move = None
    for i in range(3):
        for j in range(3):
            if board[i][j] == None:
                board[i][j] = ai
                move_val = minimax(board, 0, -math.inf, math.inf, False)
                board[i][j] = None
                if best_val is None or move_val > best_val:
                    best_val = move_val
                    best_move = [i, j]
    return best_move

# Main function to determine the next move and check the game status
def main_solution(board_state):
    board = board_state
    if check_winner(board) is None:
        best_move = find_best_move(board)
        board[best_move[0]][best_move[1]] = ai
    result = check_winner(board)
    return {"board": board, "result": result}

