class Network():
    def __init__(self, nodes, edges):
        self.nodes = set()
        self.edges = set()
        self.n_nodes = 0
        self.n_edges = 0
        self.adj = {}
        self.add_nodes(nodes)
        self.add_edges(edges)

    def add_nodes(self, nodes):
        self.nodes.update(nodes)
        for node in nodes:
            self.adj[node] = set()
        self.n_nodes = self.calc_n_nodes()

    def add_edges(self, edges):
        self.edges.update(edges)
        for edge in edges:
            self.adj[edge[0]].add(edge[1])
            self.adj[edge[1]].add(edge[0])
        self.n_edges = self.calc_n_edges()

    def calc_n_nodes(self):
        return len(self.nodes)

    def calc_n_edges(self):
        return len(self.edges)

    def degree(self, node):
        return len(self.adj[node])

    def max_degree(self):
        degrees = set()
        for node in self.nodes:
            degrees.add(self.degree(node))
        return max(degrees)

    def clustering_coefficient(self, node):
        degree = self.degree(node)
        if degree <= 1:
            raise Exception("node has no edges, therefore no clustering coefficient")
        n_neighbor_edges = 0.0
        neighbors = list(self.adj[node])
        for i in range(0, len(neighbors)):
            for j in range(i+1, len(neighbors)):
                if (neighbors[i], neighbors[j]) in self.edges or (neighbors[j], neighbors[i]) in self.edges:
                    n_neighbor_edges += 1.0
        cc = 2.0 * n_neighbor_edges / (degree * (degree - 1.0))
        return cc

    def max_cc(self):
        ccs = set()
        for node in self.nodes:
            try:
                ccs.add(self.clustering_coefficient(node))
            except Exception:
                pass
        return max(ccs) if ccs else 0.0

    def n_triangles(self):
        n_triangles = 0.0
        finished_nodes = set()
        for node in self.adj:
            neighbors = list(self.adj[node].difference(finished_nodes))
            for i in range(0, len(neighbors)):
                for j in range(i + 1, len(neighbors)):
                    if (neighbors[i], neighbors[j]) in self.edges or (neighbors[j], neighbors[i]) in self.edges:
                        n_triangles += 1.0
            finished_nodes.add(node)
        return n_triangles

# main function
def main_solution(nodes, edges):
    # Convert JSON serializable inputs to original input variables
    nodes = set(nodes)
    edges = set(tuple(edge) for edge in edges)

    # Create network instance
    network = Network(nodes, edges)

    # Calculate the maximum clustering coefficient
    max_clustering_coefficient = network.max_cc()

    # Calculate the number of triangles in the network
    num_triangles = network.n_triangles()

    # Convert outputs to JSON serializable format
    result = {
        "max_clustering_coefficient": max_clustering_coefficient,
        "num_triangles": num_triangles
    }

    return result

