def restore_matrix(matrix):
    matrix_map = dict()
    matrix = matrix.split("\n")
    (m, n) = matrix[0].split(" ")
    for row in matrix[1:]:
        (x, y, value) = row.split(" ")
        matrix_map[(int(x), int(y))] = float(value)
    return (int(m), int(n), matrix_map)

def multiply(m1, m2):
    if (m1[1] != m2[0]):
        raise ValueError("Can't multiply: [{},{}] [{},{}]".format(m1[0], m1[1], m2[0], m2[1]))
    m_result = dict()
    for i in range(1, m1[0]+1):
        for j in range(1, m2[1]+1):
            m_result[(i, j)] = 0
            for k in range(1, m1[1]+1):
                m_result[(i, j)] += float(m1[2].get((i, k), 0) * m2[2].get((k, j), 0))
            if (m_result[(i, j)] == 0):
                del m_result[(i, j)]
    return (m1[0], m2[1], m_result)

# main function
def main_solution(matrix1_str, matrix2_str):
    # Convert JSON serializable inputs to original input variables
    m1 = restore_matrix(matrix1_str)
    m2 = restore_matrix(matrix2_str)

    # Perform the multiplication
    m_result = multiply(m1, m2)

    # Convert the result to JSON serializable output
    result_str = f"{m_result[0]} {m_result[1]}\n"
    for key, value in m_result[2].items():
        result_str += f"{key[0]} {key[1]} {value}\n"

    return result_str.strip()

