    def __init__(self):
        self.vertices = []
        self.edges = {}
        self.UI = UserInterface()
        self.reason = None

    def create_vertex(self, v):
        if v != "":
            self.vertices.append(v)

    def _create_dict(self):
        for i in self.vertices:
            self.edges[i] = []

    def create_edge(self, v1, v2):
        if len(self.edges) == 0:
            self._create_dict()
        if v1 in self.vertices and v2 in self.vertices:
            for i in self.edges:
                if i == v1 and v2 not in self.edges[i]:
                    self.edges[i].append(v2)
                    self.edges[v2].append(i)
                    return True

    def get_degree(self, v):
        if v in self.edges:
            return len(self.edges[v])

    def has_circuit(self):
        for i in self.edges:
            if (self.get_degree(i) == 0) or (self.get_degree(i) % 2 != 0):
                self.reason = "The graph does not have an Euler Circuit, deg(" + i + ") is odd or 0."
                return False
        if len(self.vertices) == 0:
            self.reason = "The graph does not have an Euler Circuit, no vertices or edges found."
            return False
        else:
            self.reason = True
            return True

    def has_trail(self):
        odd = 0
        for i in self.edges:
            if (self.get_degree(i) % 2 != 0):
                odd += 1
        if odd == 2:
            self.reason = True
            return True
        else:
            self.reason = "The graph does not have an Euler trail. Must be exactly 2 vertices with odd degree."
            return False

