from typing import List, Dict

# Definition for a binary tree node.
class TreeNode:
    def __init__(self, x):
        self.val = x
        self.left = None
        self.right = None

# Function to convert a list to a binary tree
def list_to_tree(lst: List[int]) -> TreeNode:
    if not lst:
        return None
    root = TreeNode(lst[0])
    queue = [root]
    i = 1
    while queue and i < len(lst):
        node = queue.pop(0)
        if lst[i] is not None:
            node.left = TreeNode(lst[i])
            queue.append(node.left)
        i += 1
        if i < len(lst) and lst[i] is not None:
            node.right = TreeNode(lst[i])
            queue.append(node.right)
        i += 1
    return root

# Function to convert a binary tree to a list
def tree_to_list(root: TreeNode) -> List[int]:
    if not root:
        return []
    queue = [root]
    lst = []
    while queue:
        node = queue.pop(0)
        if node:
            lst.append(node.val)
            queue.append(node.left)
            queue.append(node.right)
        else:
            lst.append(None)
    while lst[-1] is None:
        lst.pop()
    return lst

# main function
