    # Convert JSON serializable inputs to TreeNode objects
    A_tree = list_to_tree(A)
    B_tree = list_to_tree(B)

    # Define the solution function
    def isSubStructure(A: TreeNode, B: TreeNode) -> bool:
        def recur(A, B):
            if not B:
                return True
            if not A or A.val != B.val:
                return False
            return recur(A.left, B.left) and recur(A.right, B.right)
        return bool(A and B) and (recur(A, B) or isSubStructure(A.left, B) or isSubStructure(A.right, B))

    # Get the result
    result = isSubStructure(A_tree, B_tree)

    # Return the result as a JSON serializable output
    return result

