# all class and function definitions in the code file, if any
def g(x):
    return mt.log10(x**2)

def bisseccao(f, a, b, erro, iteracoes):
    it = 0
    x = b
    er = 10**4
    while er >= erro and it < iteracoes:
        xold = x
        x = (a + b)/2
        er = abs((x - xold)/x)
        if f(a) * f(x) < 0:
            b = x
        if f(b) * f(x) < 0:
            a = x
        if f(a) * f(b) > 0:
            return 'Não é possível aplicar o método da Bisseção neste intervalo', er, it
        it += 1
    return x, er, it

# main function
def main_solution(a, b, erro, iteracoes):
