    a = float(a)
    b = float(b)
    erro = float(erro)
    iteracoes = int(iteracoes)

    # Call the bisseccao function with the function g
    root, error, iterations = bisseccao(g, a, b, erro, iteracoes)

    # Convert the output to JSON serializable format
    if isinstance(root, str):
        result = root
    else:
        result = {
            "root": root,
            "error": error,
            "iterations": iterations
        }

