import math
import copy
import random

# Constants
X = "X"
O = "O"
EMPTY = None

# Function to initialize the board
def initial_state():
    return [[EMPTY, EMPTY, EMPTY],
            [EMPTY, EMPTY, EMPTY],
            [EMPTY, EMPTY, EMPTY]]

# Function to determine the next player
def player(board):
    number_of_Xs = sum(row.count(X) for row in board)
    number_of_Os = sum(row.count(O) for row in board)
    return X if number_of_Xs <= number_of_Os else O

# Function to get all possible actions
def actions(board):
    return {(i, j) for i, row in enumerate(board) for j, value in enumerate(row) if value == EMPTY}

# Function to get the result of an action
def result(board, action):
    copy_board = copy.deepcopy(board)
    copy_board[action[0]][action[1]] = player(board)
    return copy_board

# Function to determine the winner
def winner(board):
    lines = [
        [(0, 0), (0, 1), (0, 2)],
        [(1, 0), (1, 1), (1, 2)],
        [(2, 0), (2, 1), (2, 2)],
        [(0, 0), (1, 0), (2, 0)],
        [(0, 1), (1, 1), (2, 1)],
        [(0, 2), (1, 2), (2, 2)],
        [(0, 0), (1, 1), (2, 2)],
        [(2, 0), (1, 1), (0, 2)],
    ]
    for line in lines:
        if all(board[i][j] == X for i, j in line):
            return X
        if all(board[i][j] == O for i, j in line):
            return O
    return None

# Function to check if the game is over
def terminal(board):
    return winner(board) is not None or not actions(board)

# Function to get the utility of the board
def utility(board):
    win = winner(board)
    if win == X:
        return 1
    elif win == O:
        return -1
    else:
        return 0

# Function to get the optimal action using minimax
def minimax(board):
    if terminal(board):
        return None
    if board == initial_state():
        return (random.randint(0, 2), random.randint(0, 2))

    if player(board) == X:
        value = -math.inf
        best_action = None
        for action in actions(board):
            min_val = min_value(result(board, action))
            if min_val > value:
                value = min_val
                best_action = action
    else:
        value = math.inf
        best_action = None
        for action in actions(board):
            max_val = max_value(result(board, action))
            if max_val < value:
                value = max_val
                best_action = action
    return best_action

# Function to get the min value of the board
def min_value(board):
    if terminal(board):
        return utility(board)
    v = math.inf
    for action in actions(board):
        v = min(v, max_value(result(board, action)))
    return v

# Function to get the max value of the board
def max_value(board):
    if terminal(board):
        return utility(board)
    v = -math.inf
    for action in actions(board):
        v = max(v, min_value(result(board, action)))
    return v

# main function
def main_solution(board_state):
    # Convert the JSON serializable input to the original input variable
    board = [row[:] for row in board_state]

    # Get the optimal action using minimax
    optimal_action = minimax(board)

    # Convert the output to JSON serializable format
    return {"optimal_action": optimal_action}

