import string
import random

# Node Class with constructor and is_wordPalindrome methods
class LLNode(object):
    def __init__(self, data):
        self.data = data
        self.next = None

    def is_wordPalindrome(self):
        if self.data is None:
            return False
        else:
            lower_word = self.data.lower()
            reversed_word = lower_word[::-1]
            return lower_word == reversed_word

# Class for Queue single LinkedList object 
class QueueLinkedList(object):
    def __init__(self):
        self.head = None
        self.tail = None

    def checkPalindrome(self): 
        cur_node = self.head
        queueSize = self.getSize()

        if self.head is None:
            return
        else:
            while queueSize >= 1:
                if cur_node.is_wordPalindrome():
                    if self.queueDeq() is not None:
                        self.queueEnq(cur_node.data)
                        cur_node = cur_node.next
                else:
                    if self.queueDeq() is not None:
                        cur_node = cur_node.next
                queueSize -= 1

    def getSize(self):
        count = 0
        cur_node = self.head
        while cur_node:
            count += 1
            cur_node = cur_node.next
        return count

    def queueToString(self):
        cur_node = self.head
        LLString = ""
        if self.head is None and self.tail is None:
            return LLString
        while cur_node:
            LLString += cur_node.data + " "
            cur_node = cur_node.next
        return LLString.strip()

    def queueEnq(self, data):
        new_node = LLNode(data)
        if self.head is None and self.tail is None:
            self.head = new_node
            self.tail = new_node
        else:
            self.tail.next = new_node
            self.tail = new_node
        return self

    def queueDeq(self):
        if self.head is None and self.tail is None:
            return None
        else:
            curr_data = self.head.data
            self.head = self.head.next
            return curr_data

# main function
def main_solution(words):
    # Convert input list to QueueLinkedList
    queue = QueueLinkedList()
    for word in words:
        queue.queueEnq(word)

    # Check for palindromes and rearrange the queue
    queue.checkPalindrome()

    # Convert the queue to a string of palindromes
    palindrome_string = queue.queueToString()

    # Return the string of palindromes
    return palindrome_string

