        # rows
        count = 0
        for i in range(3):
            s = m[count] + m[count + 1] + m[count + 2]
            if s == 3:
                return 1
            elif s == -3:
                return -1
            count = count + 3
        # columns
        count = 0
        for i in range(3):
            s = m[count] + m[count + 3] + m[count + 6]
            if s == 3:
                return 1
            elif s == -3:
                return -1
            count = count + 1
        # diagonals
        if m[0] + m[4] + m[8] == 3 or m[2] + m[4] + m[6] == 3:
