# all class and function definitions in the code file, if any
def shift(A, n):
    if n == 0:
        return A
    return [0] + shift(A, n-1)

def mult(X, Y):
    if zero(Y):
        return [0]
    Z = mult(X, div2(Y))
    if even(Y):
        return add(Z, Z)
    else:
        return add(X, add(Z, Z))

def zero(X):
    if len(X) == 0:
        return True
    else:
        for j in range(len(X)):
            if X[j] == 1:
                return False
    return True

def div2(Y):
    if len(Y) == 0:
        return Y
    else:
        return Y[1:]

def even(X):
    if ((len(X) == 0) or (X[0] == 0)):
        return True
    else:
        return False

def add(A, B):
    A1 = A[:]
    B1 = B[:]
    n = len(A1)
    m = len(B1)
    if n < m:
        for j in range(len(B1)-len(A1)):
            A1.append(0)
    else:
        for j in range(len(A1)-len(B1)):
            B1.append(0)
    N = max(m, n)
    C = []
    carry = 0
    for j in range(N):
        C.append(exc_or(A1[j], B1[j], carry))
        carry = nextcarry(carry, A1[j], B1[j])
    if carry == 1:
        C.append(carry)
    return C

def exc_or(a, b, c):
    return (a ^ (b ^ c))

def nextcarry(a, b, c):
    if ((a & b) | (b & c) | (c & a)):
        return 1
    else:
        return 0

def bin2dec(A):
    if len(A) == 0:
        return 0
    val = A[0]
    pow = 2
    for j in range(1, len(A)):
        val = val + pow * A[j]
        pow = pow * 2
    return val

def reverse(A):
    B = A[::-1]
    return B

def trim(A):
    if len(A) == 0:
        return A
    A1 = reverse(A)
    while ((not (len(A1) == 0)) and (A1[0] == 0)):
        A1.pop(0)
    return reverse(A1)

def compare(A, B):
    A1 = reverse(trim(A))
    A2 = reverse(trim(B))
    if len(A1) > len(A2):
        return 1
    elif len(A1) < len(A2):
        return 2
    else:
        for j in range(len(A1)):
            if A1[j] > A2[j]:
                return 1
            elif A1[j] < A2[j]:
                return 2
        return 0

def dec2bin(n):
    if n == 0:
        return []
    m = n // 2
    A = dec2bin(m)
    fbit = n % 2
    return [fbit] + A

def sub(A, B):
    A1 = A[:]
    B1 = B[:]
    n = len(A1)
    m = len(B1)
    if n < m:
        for j in range(len(B1)-len(A1)):
            A1.append(0)
    else:
        for j in range(len(A1)-len(B1)):
            B1.append(0)
    B1 = twoComplement(B1)
    B1 = add(B1, [1])
    N = max(m, n)
    C = []
    carry = 0
    for j in range(N):
        C.append(exc_or(A1[j], B1[j], carry))
        carry = nextcarry(carry, A1[j], B1[j])
    return C

def twoComplement(a):
    for i in range(len(a)):
        if a[i] & 1 == 1:
            a[i] = 0
        else:
            a[i] = 1
    return a

def expo(a, b):
    if b == dec2bin(1):
        return a
    if b == dec2bin(2):
        return mult(a, a)
    if even(b):
        return expo(expo(a, div2(b)), [0, 1])
    else:
        return mult(a, expo(expo(a, div2(sub(b, [1]))), dec2bin(2)))

# main function
def main_solution(a, b, c, d):
    a_bin = dec2bin(a)
    b_bin = dec2bin(b)
    c_bin = dec2bin(c)
    d_bin = dec2bin(d)

    aRaisedb = expo(a_bin, b_bin)
    cRaisedd = expo(c_bin, d_bin)

    if compare(aRaisedb, cRaisedd) == 2:
        remainder = sub(cRaisedd, aRaisedb)
        return {"result": -bin2dec(remainder)}
    else:
        remainder = sub(aRaisedb, cRaisedd)
        return {"result": bin2dec(remainder)}

