import json

# all class and function definitions in the code file, if any
class TreeNode:
    def __init__(self, x):
        self.val = x
        self.left = None
        self.right = None

class Solution:
    def find_path(self, root, num):
        if not root:
            return None
        result = []

        def core(root, path, cur):
            cur += root.val
            path.append(root.val)

            # 判断是否到达叶节点
            flag = (root.left == None and root.right == None)

            if flag and cur == num:
                result.append(path[:])

            if cur < num:
                if root.left:
                    core(root.left, path, cur)
                if root.right:
                    core(root.right, path, cur)
            path.pop() # pop is to delete this visited path

        core(root, [], 0)
        return result

# main function
def main_solution(tree_structure, target_sum):
    # Convert JSON serializable input to original input variables
    def build_tree(nodes):
        if not nodes:
            return None
        root = TreeNode(nodes[0])
        queue = [root]
        i = 1
        while queue and i < len(nodes):
            node = queue.pop(0)
            if nodes[i] is not None:
                node.left = TreeNode(nodes[i])
                queue.append(node.left)
            i += 1
            if i < len(nodes) and nodes[i] is not None:
                node.right = TreeNode(nodes[i])
                queue.append(node.right)
            i += 1
        return root

    root = build_tree(tree_structure)
    solution = Solution()
    paths = solution.find_path(root, target_sum)

    # Convert output to JSON serializable format
    return json.dumps(paths)

