            return [int(i > 0 and i < 7 and cells[i-1] == cells[i+1]) for i in range(8)]

        seen = {}
        while N > 0:
            c = tuple(cells)
            if c in seen:
                N %= seen[c] - N
            seen[c] = N

            if N >= 1:
                N -= 1
                cells = nextDay(cells)

        return cells

