class minimax:
    def __init__(self, matrix):
        self.matrix = matrix
        self.row = -1
        self.col = -1

    def is_full(self):
        cnt = 0
        for i in range(3):
            for j in range(3):
                cnt += (self.matrix[i][j] == '-')
        return cnt == 0

    def reward(self):
        # row checker
        for i in range(3):
            sc1, sc2 = 0, 0
            for j in range(3):
                sc1 += (self.matrix[i][j] == 'O')
                sc2 += (self.matrix[i][j] == '+')
            if sc1 == 3:
                return 1
            if sc2 == 3:
                return -1

        # col checker
        for i in range(3):
            sc1, sc2 = 0, 0
            for j in range(3):
                sc1 += (self.matrix[j][i] == 'O')
                sc2 += (self.matrix[j][i] == '+')
            if sc1 == 3:
                return 1
            if sc2 == 3:
                return -1

        # diagonal1 checker
        sc1, sc2 = 0, 0
        for i in range(3):
            sc1 += (self.matrix[i][i] == 'O')
            sc2 += (self.matrix[i][i] == '+')
        if sc1 == 3:
            return 1
        if sc2 == 3:
            return -1

        # diagonal2 checker
        sc1, sc2 = 0, 0 
        for i in range(3):
            sc1 += (self.matrix[i][(2-i)] == 'O')
            sc2 += (self.matrix[i][(2-i)] == '+')
        if sc1 == 3:
            return 1
        if sc2 == 3:
            return -1
        return 0

    def get_row_col(self, mx):
        if mx:
            best = [-1000, -1, -1]
        else:
            best = [1000, -1, -1]

        if self.is_full() or self.reward() in [-1, 1]:
            return [self.reward(), -1, -1] 

        if mx:
            for i in range(3):
                for j in range(3):
                    if self.matrix[i][j] == '-':
                        self.matrix[i][j] = 'O'
                        score = self.get_row_col(False)
                        self.matrix[i][j] = '-'
                        score[1], score[2] = i+1, j+1
                        if score[0] > best[0]:
                            best = score
            return best

        if not mx:
            for i in range(3):
                for j in range(3):
                    if self.matrix[i][j] == '-':
                        self.matrix[i][j] = '+'
                        score = self.get_row_col(True)
                        self.matrix[i][j] = '-'
                        score[1], score[2] = i+1, j+1
                        if score[0] < best[0]:
                            best = score
            return best

# main function
def main_solution(matrix):
    # Convert the input matrix to a list of lists if it's not already
    if not isinstance(matrix, list):
        raise ValueError("Input matrix must be a list of lists")

    env = create_env(matrix)
    A = minimax(env.matrix)
    best = A.get_row_col(True)

    # Return the best move as a dictionary
    return {"row": best[1], "col": best[2]}

