class LinkedList(object):
    class Node(object):
        def __init__(self, data=None, nxt=None):
            self.data = data
            self.nxt = nxt

    @staticmethod
    def insert_after(linked_list, node, new_node):
        new_node.nxt = node.nxt
        node.nxt = new_node
        if linked_list.tail == node:
            linked_list.tail = new_node

    @staticmethod
    def delete_after(linked_list, node):
        if linked_list.tail:
            if node.nxt == linked_list.tail:
                linked_list.tail = node
            node.nxt = node.nxt.nxt

    def __init__(self):
        self.tail = None

    def search(self, key):
        if self.tail:
            x = self.tail.nxt
            if x.data == key:
                return x
            else:
                x = x.nxt
                while (x != self.tail.nxt) and (x.data != key):
                    x = x.nxt
                return x if x != self.tail.nxt else None
        return None

    def insert(self, x):
        if self.tail:
            x.nxt = self.tail.nxt
            self.tail.nxt = x
        else:
            self.tail = x
            self.tail.nxt = self.tail

    def delete(self, x):
        if not self.tail:
            return False
        if self.tail.nxt == self.tail:
            if self.tail == x:
                self.tail = None
                return True
            return False
        if self.tail.nxt == x:
            self.tail.nxt = self.tail.nxt.nxt
            return True
        else:
            node = self.tail.nxt
            while node.nxt != self.tail.nxt and node.nxt != x:
                node = node.nxt
            if node.nxt != self.tail.nxt:
                node.nxt = x.nxt
                return True
            return False

    def insert_by_data(self, data):
        new_node = LinkedList.Node(data)
        self.insert(new_node)

    def delete_by_key(self, key):
        x = self.search(key)
        if x:
            return self.delete(x)
        return False

    def append(self, x):
        if self.tail:
            LinkedList.insert_after(self, self.tail, x)
        else:
            self.insert(x)

# main function
def main_solution(operations):
    # Initialize the linked list
    linked_list = LinkedList()

    # Process each operation
    results = []
    for op in operations:
        if op['type'] == 'insert':
            linked_list.insert_by_data(op['data'])
        elif op['type'] == 'delete':
            linked_list.delete_by_key(op['key'])
        elif op['type'] == 'search':
            node = linked_list.search(op['key'])
            results.append(node.data if node else None)

    # Return the results of search operations
    return results

