    def dfs(s, length, pos, L, k):
        if s == k:
            return True
        for i in range(pos + 1, len(dataint)):
            if v[i]:
                continue
            if length + dataint[i] == L:
                v[i] = True
                if dfs(s + 1, 0, -1, L, k):
                    return True
                v[i] = False
