            elif dataint[i] + length < L:
                v[i] = True
                if dfs(s, length + dataint[i], i, L, k):
                    return True
                v[i] = False
                if length == 0:
                    return False
        return False

    # Iterate over possible lengths to find the smallest one that fits
    for L in range(maxlen, totallen + 1):
