# all class and function definitions in the code file, if any
class Board:
    def __init__(self):
        self.board = [0] * 10

    def makeMove(self, letter, move):
        if self.board[move] == 0:
            self.board[move] = letter
            return True
        return False

    def isWinner(self, le):
        win_conditions = [
            (1, 2, 3), (4, 5, 6), (7, 8, 9),  # rows
            (1, 4, 7), (2, 5, 8), (3, 6, 9),  # columns
            (1, 5, 9), (3, 5, 7)              # diagonals
        ]
        for a, b, c in win_conditions:
            if self.board[a] == self.board[b] == self.board[c] == le:
                return True
        return False

    def isBoardFull(self):
        return all(self.board[i] != 0 for i in range(1, 10))

    def getBoardCopy(self):
        dupeBoard = Board()
        dupeBoard.board = self.board[:]
        return dupeBoard

class TTTGame:
    def __init__(self):
        self.board = Board()
        self.playerLetter = 1  # 1 = 'X', 2 = 'O', default is 'X'
        self.computerLetter = 2
        self.setLetter = False

    def setPLetter(self, le):
        if self.setLetter:
            return 'Done'
        self.playerLetter = le
        self.computerLetter = 3 - le  # Toggle between 1 and 2
        if self.playerLetter == 2:
            self.makeComputerMove()
        self.setLetter = True
        return 'Set'

    def makeComputerMove(self):
        move = self.getComputerMove()
        self.board.makeMove(self.computerLetter, move)

    def getComputerMove(self):
        for i in range(1, 10):
            if self.board.board[i] == 0 and self.testWinMove(self.board, self.computerLetter, i):
                return i
        for i in range(1, 10):
            if self.board.board[i] == 0 and self.testWinMove(self.board, self.playerLetter, i):
                return i
        for i in range(1, 10):
            if self.board.board[i] == 0 and self.testForkMove(self.computerLetter, i):
                return i
        for i in range(1, 10):
            if self.board.board[i] == 0 and self.testForkMove(self.playerLetter, i):
                return i
        if self.board.board[5] == 0:
            return 5
        corners = [1, 3, 7, 9]
        random.shuffle(corners)
        for corner in corners:
            if self.board.board[corner] == 0:
                return corner
        sides = [2, 4, 6, 8]
        random.shuffle(sides)
        for side in sides:
            if self.board.board[side] == 0:
                return side

    def testWinMove(self, board, letter, i):
        bCopy = board.getBoardCopy()
        bCopy.board[i] = letter
        return bCopy.isWinner(letter)

    def testForkMove(self, letter, i):
        bCopy = self.board.getBoardCopy()
        bCopy.board[i] = letter
        winningMoves = 0
        for j in range(1, 10):
            if self.testWinMove(bCopy, letter, j) and bCopy.board[j] == 0:
                winningMoves += 1
        return winningMoves >= 2

# main function
def main_solution(player_letter, player_moves):
    game = TTTGame()
    game.setPLetter(player_letter)

    for move in player_moves:
        if game.board.makeMove(game.playerLetter, move):
            if game.board.isWinner(game.playerLetter):
                return 'Player Won'
            if game.board.isBoardFull():
                return 'Tie'
            game.makeComputerMove()
            if game.board.isWinner(game.computerLetter):
                return 'Computer Won'
            if game.board.isBoardFull():
                return 'Tie'
        else:
            return 'Invalid Move'

    return 'Game Ongoing'

