# import necessary packages
import random
from collections import deque

# all class and function definitions in the code file, if any
class Graph:
    def __init__(self):
        self.vertices = {}
        self.edges = {}
        self.k = -1
        self.X = -1
        self.Y = -1
        self.Z = -1
        self.END = -1

    def add_vertices(self, vertex_list):
        for v in vertex_list:
            self.vertices[v] = Node(v)

    def get_neighbors(self, vertex):
        return self.vertices[vertex].neighbors

    def is_path_free(self, path):
        for i in range(len(path) - 1):
            e1 = (path[i], path[i+1])
            e2 = (path[i+1], path[i])
            if e1 in self.edges:
                e = self.edges[e1]
                if e.reserved:
                    return False
            else:
                e = self.edges[e2]
                if e.reserved:
                    return False
        return True

    def reserve_path(self, path):
        for i in range(len(path) - 1):
            e1 = (path[i], path[i+1])
            e2 = (path[i+1], path[i])
            if e1 in self.edges:
                e = self.edges[e1]
                e.set_reserved()
            else:
                e = self.edges[e2]
                e.set_reserved()

    def pair_shortest_paths(self, v1, v2):
        queue = deque()
        v1_list = [v1]
        queue.append(v1_list)

        answers = []
        stop = False
        while len(queue) > 0:
            path = queue.popleft()
            x = path[-1]
            for y in self.get_neighbors(x):
                if y == v2:
                    temp = path.copy()
                    temp.append(y)
                    answers.append(temp)
                stop |= y == v2
                if not stop:
                    temp = path.copy()
                    temp.append(y)
                    queue.append(temp)
        return answers

    def get_pod(self, vertex):
        return ((vertex - self.Z) // (self.k // 2)) // (self.k // 2) 

    def get_switch_from_server(self, vertex, pod):
        z_index = (vertex - self.Z) // (self.k // 2)
        return self.Y + z_index

    def get_switch_from_core(self, core, pod):
        half_k = self.k // 2
        core_index = core // half_k
        return self.X + (pod * half_k) + core_index

    def get_core_path(self, core, v1, v2):
        pod1 = self.get_pod(v1)
        pod2 = self.get_pod(v2)
        top_switch_1 = self.get_switch_from_core(core, pod1)
        top_switch_2 = self.get_switch_from_core(core, pod2)
        bottom_switch_1 = self.get_switch_from_server(v1, pod1)
        bottom_switch_2 = self.get_switch_from_server(v2, pod2)
        return [v1, bottom_switch_1, top_switch_1, core, top_switch_2, bottom_switch_2, v2]

    def get_path_linear_cores(self, v1, v2):
        assert(v1 != v2)
        if (self.get_pod(v1) == self.get_pod(v2)):
            return None
        for i in range (0, (self.k ** 2) // 4):
            path = self.get_core_path(i, v1, v2)
            if self.is_path_free(path):
                self.reserve_path(path)
                return path
        return None

    def add_edge(self, e):
        self.edges[e] = Edge(e)
        v1 = self.vertices.get(e[0])
        v1.add_neighbor(e[1])
        v2 = self.vertices.get(e[1])
        v2.add_neighbor(e[0])

    def add_edges(self, edge_list):
        for e in edge_list:
            self.add_edge(e)

class Node:
    BUFFER_SIZE = 200
    def __init__(self, label):
        self.name = label
        self.neighbors = set()
        self.queue = deque()
        self.dropped_set = set()

    def __eq__(self, other):
        return self.name == other.name
    def __str__(self):
        return str(self.name)
    def __hash__(self):
        return hash(self.name)

    def add_neighbor(self, n):
        self.neighbors.add(n)

    def degree(self):
        return len(self.neighbors)

class Edge:
    def __init__(self, e):
        self.first = e[0]
        self.second = e[1]
        self.rate = 1
        self.reserved = False
    def __eq__(self, other):
        return self.first == other.first and self.second == other.second
    def __str__(self):
        return "{} , {}".format(self.first, self.second)
    def __hash__(self):
        return hash((self.first, self.second))
    def set_reserved(self):
        assert(not self.reserved)
        self.reserved = True

def build_fat_tree(k):
    g = Graph()
    g.k = k
    X = (k**2 ) // 4
    Y = X + (k ** 2) // 2
    Z = Y + (k ** 2) // 2
    END = Z + (k ** 3) // 4
    g.X = X
    g.Y = Y
    g.Z = Z
    g.END = END
    g.add_vertices(range(END))

    temp = Y
    for i in range(Z, END, k // 2):
        g.add_edges([(temp, server) for server in range(i, i + k // 2)])
        temp += 1

    for i in range(Y, Z):
        index = (i - Y) // (k // 2)
        pod = (index * (k // 2)) + X
        g.add_edges([(i, first_agg) for first_agg in range(pod, pod + k // 2)])

    for i in range(0, X, k//2):
        core_nodes = range(i, i + k//2)
        for j in range(X + (i // (k // 2)), Y, k // 2):
            g.add_edges([(j, core_node) for core_node in core_nodes])

    return g

# main function
def main_solution(k, v1, v2):
    # Build the fat tree graph
    graph = build_fat_tree(k)

    # Find the shortest path between v1 and v2
    path = graph.get_path_linear_cores(v1, v2)

    # Convert the path to a JSON serializable format
    if path is None:
        return None
    return path

