import random

# main function
def main_solution(start_year, end_year):
    # Initialize the weekday for 1 Jan 1900 (Monday)
    weekday = 2
    total_sundays = 0

    # Loop through each year from start_year to end_year
    for year in range(start_year, end_year + 1):
        for month in range(1, 12 + 1):
            if month in (4, 6, 9, 11):
                days = 30
            elif month in (1, 3, 5, 7, 8, 10, 12):
                days = 31
            elif year % 4 == 0 and not (year % 100 == 0 and year % 400 != 0):
                days = 29
            else:
                days = 28

            # Adjust weekday for the next month
            while days >= 0:
                days -= 7

            while days < 0:
                weekday -= 1
                if weekday == 0:
                    weekday = 7
                days += 1

            # Check if the first day of the month is a Sunday and count it
            if weekday == 1 and year >= start_year:
                total_sundays += 1

    # Return the total number of Sundays that fell on the first of the month
    return total_sundays

