import math

# all class and function definitions in the code file, if any
def trapecoidal_rule(f, a, b, n):
    h = float((b - a) / n)
    I = 0
    for i in range(n):
        I += f(a + i * h) + f(a + ((i + 1) * h))
    I *= 1 / 2 * h
    return I

def f_1(x):
    return 2 * x + 1

def f_2(x):
    return x ** 2

def f_3(x):
    return 3 / 2 * (math.sin(x)) ** 3

# main function
def main_solution(function_name, a, b, n, exact_integral):
    # Convert function_name to the corresponding function
    if function_name == "f_1":
        f = f_1
    elif function_name == "f_2":
        f = f_2
    elif function_name == "f_3":
        f = f_3
    elif function_name == "cos":
        f = math.cos
    else:
        raise ValueError("Invalid function name")

    # Calculate the approximate integral using the trapezoidal rule
    approx_integral = trapecoidal_rule(f, a, b, n)

    # Calculate the error
    error = math.fabs(exact_integral - approx_integral)

    # Return the result as a dictionary
    return {
        "approx_integral": approx_integral,
        "error": error
    }

