def orderNext_getTile(linkList):
    listLen = len(linkList)
    harftLen = listLen // 2
    for i in range(1, harftLen + 1):
        part = linkList[0:i]
        tern = int(listLen / i)
        last = listLen - tern * i
        leave = linkList[i * tern:]
        snos = True
        for j in range(1, tern):
            start = i * j
            if part != linkList[start:start + i]:
                snos = False
                break
        for o in range(0, last):
            if part[o] != leave[o]:
                snos = False
                break
        if snos:
            return part
    return False

# Generator to infinitely repeat a given sequence
def orderNext_reTile(tileList):
    long = len(tileList)
    i = 0
    while True:
        yield tileList[i]
        i += 1
        if i >= long:
            i = 0

# Function to generate a sequence based on a given pattern
def getNext_fromLinear(numList):
    le_s = []
    le_n = []

    for i in numList:
        if i >= 0:
            le_s.append('+')
            le_n.append(i)
        elif i < 0:
            le_s.append('-')
            le_n.append(-i)
        else:
