import random

# all class and function definitions in the code file, if any
def bisection(a, b, epsilon, fun):
    c = a
    while b - a > epsilon:
        c = (b + a) / 2
        if fun(c) == 0:
            break
        elif fun(c) > 0:
            b = c
        else:
            a = c
    return c

def newton_raphson(x, epsilon, fun, derivative):
    h = fun(x) / derivative(x)
    while abs(h) > epsilon:
        h = fun(x) / derivative(x)
        x -= h
    return x

def derivative(x):
    return 3 * x * x - 2 * x

def fun(x):
    return x ** 3 - x ** 2 + 2

# main function
def main_solution(method, a, b, x, epsilon):
    # Convert JSON serializable inputs to original input variables
    if method == "bisection":
        result = bisection(a, b, epsilon, fun)
    elif method == "newton_raphson":
        result = newton_raphson(x, epsilon, fun, derivative)
    else:
        raise ValueError("Invalid method specified")

    # Convert the result to a JSON serializable output
    return {"result": result}

