# import necessary packages
import random
import math

# dictionary mapping
letters = {chr(i): i for i in range(256)}
numbers = {num: letter for letter, num in letters.items()}

def exponentMod(A, B, C):
    # Base cases 
    if A == 0:
        return 0
    if B == 0:
        return 1

    # If B is even 
    if B % 2 == 0:
        y = exponentMod(A, B / 2, C)
        y = (y * y) % C

    # If B is odd 
    else:
        y = A % C
        y = (y * exponentMod(A, B - 1, C) % C) % C

    return (int)((y + C) % C)

def isPrime(n):
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True

def gcd(a, b):
    if b == 0:
        return a
    else:
        return gcd(b, a % b)

def gen_public_key(n, phi):
    list_random_e = [i for i in range(1, phi) if (n % i != 0 and gcd(i, phi) == 1)]
    e = random.choice(list_random_e)
    return (n, e)

def gen_private_key(n, phi, e):
    list_random_k = [i for i in range(1, phi) if (((i * phi) + 1) % e == 0)]
    k = random.choice(list_random_k)
    d = ((k * phi) + 1) // e
    return (n, d)

def change(c):
    d = ""
    while c > 0:
        digit = c % 10
        e = random.randint(0, 9)
        d += str(e) + str(digit)  # adding random then actual digit , on reversing we get actual digit followed by random no
        c = c // 10
    return d[::-1]

def changeback(i):
    original = ""
    for n in range(0, len(i)):
        if n % 2 == 0:
            original += i[n]
    return int(original)

# main function
def main_solution(message):
    # Generate primes and keys
    primes = [i for i in range(26, 1000) if isPrime(i)]
    p = random.choice(primes)
    primes.remove(p)
    q = random.choice(primes)
    n = p * q
    phi = (p - 1) * (q - 1)
    public_key = gen_public_key(n, phi)
    private_key = gen_private_key(n, phi, public_key[1])

    # Encrypt the message
    encrypted_message = []
    for char in message:
        c = exponentMod(letters[char], public_key[1], n)
        s = change(c)
        encrypted_message.append(s)

    # Decrypt the message
    decrypted_message = []
    for s in encrypted_message:
        cipher = changeback(s)
        decryp = exponentMod(cipher, private_key[1], n)
        dmsg = numbers[decryp]
        decrypted_message.append(dmsg)

    # Return the decrypted message as a string
    return ''.join(decrypted_message)

