def periodicity(a):
    if not a:
        return 0
    for i in range(1, len(a) // 2 + 1):
        if all([a[j:j + i] == a[:i] for j in range(0, min(5, len(a) // i), i)]):
            return i
    return 0

def find_periodicity(N):
    a0 = int(sqrt(N))
    tmp = sqrt(N)
    a = []
    step = 0
    while periodicity(a) == 0 or step < 100:
        tmp = 1 / (tmp % 1) 
        a.append(int(tmp))
        step += 1
    return periodicity(a)

