import copy
import random

# Constants
X = "X"
O = "O"
EMPTY = None

# Function to initialize the board
def initial_state():
    return [[EMPTY, EMPTY, EMPTY],
            [EMPTY, EMPTY, EMPTY],
            [EMPTY, EMPTY, EMPTY]]

# Function to determine the next player
def player(board):
    count = sum(cell is not EMPTY for row in board for cell in row)
    return O if count % 2 != 0 else X

# Function to get all possible actions
def actions(board):
    return {(i, j) for i in range(3) for j in range(3) if board[i][j] == EMPTY}

# Function to get the result of an action
def result(board, action):
    if terminal(board):
        raise ValueError("Game over.")
    elif action not in actions(board):
        raise ValueError("Invalid action.")
    else:
        p = player(board)
        result_board = copy.deepcopy(board)
        (i, j) = action
        result_board[i][j] = p
    return result_board

# Function to determine the winner
def winner(board):
    lines = [
        [(0, 0), (0, 1), (0, 2)],
        [(1, 0), (1, 1), (1, 2)],
        [(2, 0), (2, 1), (2, 2)],
        [(0, 0), (1, 0), (2, 0)],
        [(0, 1), (1, 1), (2, 1)],
        [(0, 2), (1, 2), (2, 2)],
        [(0, 0), (1, 1), (2, 2)],
        [(0, 2), (1, 1), (2, 0)],
    ]
    for line in lines:
        (a, b, c) = line
        if board[a[0]][a[1]] == board[b[0]][b[1]] == board[c[0]][c[1]] != EMPTY:
            return board[a[0]][a[1]]
    return None

# Function to check if the game is over
def terminal(board):
    return winner(board) is not None or all(cell is not EMPTY for row in board for cell in row)

# Function to get the utility of the board
def utility(board):
    w = winner(board)
    if w == X:
        return 1
    elif w == O:
        return -1
    else:
        return 0

# Main function to solve the problem
def main_solution(board):
    """
    Determines the optimal next move for the current player on the given board.

    Input:
    board (list of lists): A 3x3 matrix representing the Tic Tac Toe board. Each cell can be 'X', 'O', or None.

    Output:
    tuple: A tuple representing the optimal move (i, j) where i and j are integers between 0 and 2.
    """
    if terminal(board):
        return None
    return minimax(board)

# Function to determine the optimal move using the minimax algorithm
def minimax(board):
    p = player(board)
    if p == X:
        v = float("-inf")
        move = None
        for action in actions(board):
            minValueResult = minValue(result(board, action))
            if minValueResult > v:
                v = minValueResult
                move = action
    elif p == O:
        v = float("inf")
        move = None
        for action in actions(board):
            maxValueResult = maxValue(result(board, action))
            if maxValueResult < v:
                v = maxValueResult
                move = action
    return move

# Helper function for the minimax algorithm
def maxValue(board):
    if terminal(board):
        return utility(board)
    v = float("-inf")
    for action in actions(board):
        v = max(v, minValue(result(board, action)))
    return v

# Helper function for the minimax algorithm
def minValue(board):
    if terminal(board):
        return utility(board)
    v = float("inf")
    for action in actions(board):
        v = min(v, maxValue(result(board, action)))
    return v

