def fib(n):
    """Find the nth Fibonacci number."""
    if (n < 1): return 0
    if (n == 1 or n == 2): return n
    thisfib = 0
    i = 3
    prevprev = 1
    prev = 2
    while (i <= n):
        thisfib = prevprev + prev
        prevprev = prev
        prev = thisfib
        i = i + 1
    return thisfib

def factorize(n):
    i=1
    factors = array('L')
    while i*i <= n:
        if n%i == 0:
            factors.append(i)
        i += 1
    if factors[-1]**2 == n:
        for i in factors[-2::-1]:
            factors.append(n/i)
    else:
        for i in factors[-1::-1]:
            factors.append(n/i)
    return factors

