def fexp1(b, e):
    if(e == 0):
        return 1
    elif(e == 1):
        return b
    elif(e < 0):
        e = -e
        b = 1/b
    acc = 1.0
    while(e>1):
        if(e % 2 == 0):
            e //= 2
        else:
            acc *= b
            e = (e-1)//2
        b *= b
    return acc * b

