import numpy as np

# all class and function definitions in the code file, if any
def forward_difference_h2(f, x, h):
    df = [(-f(xi + 2 * h) + 4 * f(xi + h) - 3 * f(xi)) / (2 * h) for xi in x]
    return df

def backward_difference_h2(f, x, h):
    df = [(f(xi - 2 * h) - 4 * f(xi - h) + 3 * f(xi)) / (2 * h) for xi in x]
    return df

def central_difference_h2(f, x, h):
    df = [(f(xi + h) - f(xi - h)) / (2 * h) for xi in x]
    return df

def richardson(f, x, n, h):
    N = np.zeros((n, n))
    for i in range(n):
        N[i, 0] = (f(x + h / np.power(2, i)) - f(x - h / np.power(2, i))) / (2 * h / np.power(2, i))
    for i in range(1, n):
        for j in range(i, n):
            N[j, i] = N[j, i - 1] + (N[j, i - 1] - N[j - 1, i - 1]) / (np.power(2, i) - 1)
    return N

# main function
def main_solution(function_str, x_values, h, method, n=None):
    """
    Compute the derivative of a given function at specified points using different numerical methods.

    Input:
        function_str (str): A string representation of the function to be evaluated.
        x_values (list of float): List of x values at which the derivative is to be computed.
        h (float): Step size for the numerical differentiation.
        method (str): Method to use for differentiation. Options are 'forward', 'backward', 'central', or 'richardson'.
        n (int, optional): Number of levels of extrapolation for Richardson's method. Required if method is 'richardson'.

    Output:
        return (list of float or numpy float array): List of derivative values at the specified x values or a 2D array for Richardson's method.
    """
    def f(x):
        return eval(function_str)

    if method == 'forward':
        return forward_difference_h2(f, x_values, h)
    elif method == 'backward':
        return backward_difference_h2(f, x_values, h)
    elif method == 'central':
        return central_difference_h2(f, x_values, h)
    elif method == 'richardson':
        if n is None:
            raise ValueError("n must be provided for Richardson's method.")
        return richardson(f, x_values[0], n, h)
    else:
        raise ValueError("Invalid method specified. Choose from 'forward', 'backward', 'central', or 'richardson'.")

